mapper.select:
SELECT COUNT(*)
FROM articleimgs
WHERE hex = #{hex,jdbcType=CHAR}

SELECT *
FROM articles
WHERE id = #{id}

select *
from articles
where id = #{share_article,jdbcType=INTEGER}

select count(id)
from comments
where comment_id = #{id,jdbcType=INTEGER}

select nickname
from users
where id = #{publisherId,jdbcType=INTEGER}

select img
from users
where id = #{publisherId,jdbcType=INTEGER}

select img_name
from articleimgs
where article_id = #{id,jdbcType=INTEGER}

select *
from articles
where id = #{id}
order by create_time desc
limit ${(page-1)*perPage},${perPage}

mapper.update:
update articles
set like_count = like_count + #{count,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

update comments
set likes = likes + #{count,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.delete:
delete
from likes
where user_id = #{userId,jdbcType=INTEGER}
  and article_id = #{articleId,jdbcType=INTEGER}

delete
from likes
where user_id = #{userId,jdbcType=INTEGER}
  and comment_id = #{commentId,jdbcType=INTEGER}

mapper.select:
select user_id
from likes
where article_id = #{id,jdbcType=INTEGER}

select user_id
from likes
where comment_id = #{id,jdbcType=INTEGER}

select count(id)
from likes
where user_id = #{userId,jdbcType=INTEGER}
  and article_id = #{articleId,jdbcType=INTEGER}

select count(id)
from likes
where user_id = #{userId,jdbcType=INTEGER}
  and comment_id = #{commentId,jdbcType=INTEGER}

select count(id)
from articles
where id = #{id,jdbcType=INTEGER}

select count(id)
from comments
where id = #{id,jdbcType=INTEGER}

mapper.update:
